// src/components/layout/Header.jsx

import { useLocation } from "react-router-dom";

export default function Header() {
  const location = useLocation();

  const titles = {
    "/": "Dashboard",
    "/manual": "Manual Sniper",
    "/auto": "Auto Sniper",
    "/scanner": "Token Scanner",
    "/watchlist": "Watchlist",
    "/settings": "Settings",
    "/logs": "Logs",
  };

  const title = titles[location.pathname] || "Sniper Bot";

  return (
    <header className="h-16 bg-panel border-b border-white/10 flex items-center justify-between px-6">
      <h2 className="text-lg font-semibold">{title}</h2>

      <div className="flex items-center gap-4">
        <div className="flex items-center gap-2">
          <span className="w-3 h-3 bg-green-500 rounded-full"></span>
          <span className="text-sm text-gray-300">Wallet Connected</span>
        </div>

        <button className="px-4 py-2 bg-accent rounded-md hover:bg-accentHover transition">
          Disconnect
        </button>
      </div>
    </header>
  );
}
