// frontend/src/App.jsx

console.log("üî• THIS IS THE REAL App.jsx");

import React, { useEffect, useState } from "react";
import usePhantom from "./hooks/usePhantom";
import backend from "./api/backend";
import { buildSwapTx } from "./api/swap";
import { sendBundle } from "./api/jito";
import { connectWS } from "./api/websocket";
import { Connection, VersionedTransaction } from "@solana/web3.js";

const RPC_ENDPOINT = "https://api.mainnet-beta.solana.com";
const SOL_MINT = "So11111111111111111111111111111111111111112";

export default function App() {
  const { wallet, publicKey, connected, connect, disconnect } = usePhantom();

  const [mint, setMint] = useState("");
  const [analysis, setAnalysis] = useState(null);
  const [loadingAnalysis, setLoadingAnalysis] = useState(false);
  const [executingBundle, setExecutingBundle] = useState(false);

  const [autoMint, setAutoMint] = useState("");
  const [autoActive, setAutoActive] = useState(false);
  const [autoOpportunities, setAutoOpportunities] = useState([]);

  const [log, setLog] = useState("");

  const addLog = (msg) => {
    const time = new Date().toLocaleTimeString();
    setLog((prev) => `[${time}] ${msg}\n` + prev);
  };

  useEffect(() => {
    connectWS((msg) => {
      if (msg.type === "opportunity") {
        setAutoOpportunities((prev) => [msg.data, ...prev]);
        addLog(`Auto opportunity detected for mint ${msg.data.mint}`);
      }
    });
  }, []);

  const analyzeToken = async () => {
    if (!mint) return;
    setLoadingAnalysis(true);
    setAnalysis(null);
    addLog(`Analyzing token: ${mint}`);

    try {
      const { data } = await backend.post("/manual/analyze", { mint });
      setAnalysis(data);
      addLog("Analysis complete.");
    } catch (err) {
      console.error(err);
      addLog("Analysis failed.");
      alert("Failed to analyze token");
    }

    setLoadingAnalysis(false);
  };

  const executeBundle = async (source = "manual", customAnalysis = null) => {
    const current = customAnalysis || analysis;

    if (!connected || !wallet || !publicKey) {
      alert("Connect Phantom first");
      return;
    }

    if (!current?.plan?.canSnipe) {
      alert("No executable plan");
      return;
    }

    const steps = current.plan.steps;
    const firstBuy = steps.find((s) => s.type === "BUY");
    const firstSell = steps.find((s) => s.type === "SELL");

    if (!firstBuy || !firstSell) {
      alert("Plan must contain BUY + SELL");
      return;
    }

    setExecutingBundle(true);
    addLog(`Executing bundle (${source}) for mint ${current.mint}...`);

    try {
      const connection = new Connection(RPC_ENDPOINT, "confirmed");

      const buyAmountLamports = Math.floor(firstBuy.amountSol * 1e9);

      // BUY
      const buySwap = await buildSwapTx({
        inputMint: SOL_MINT,
        outputMint: current.mint,
        amount: buyAmountLamports,
        slippageBps: 5000,
        userPublicKey: publicKey
      });

      const buyTxBuf = Buffer.from(buySwap.swapTransaction, "base64");
      const buyTx = VersionedTransaction.deserialize(buyTxBuf);
      const signedBuy = await wallet.signTransaction(buyTx);
      const rawBuy = Buffer.from(signedBuy.serialize()).toString("base64");
      addLog("BUY transaction signed.");

      // SELL
      const sellSwap = await buildSwapTx({
        inputMint: current.mint,
        outputMint: SOL_MINT,
        amount: 1,
        slippageBps: 5000,
        userPublicKey: publicKey
      });

      const sellTxBuf = Buffer.from(sellSwap.swapTransaction, "base64");
      const sellTx = VersionedTransaction.deserialize(sellTxBuf);
      const signedSell = await wallet.signTransaction(sellTx);
      const rawSell = Buffer.from(signedSell.serialize()).toString("base64");
      addLog("SELL transaction signed.");

      // Jito bundle
      const bundleResult = await sendBundle([rawBuy, rawSell]);
      addLog("Jito bundle sent.");
      addLog(JSON.stringify(bundleResult, null, 2).slice(0, 800));

      alert("Bundle executed (check logs for details).");
    } catch (err) {
      console.error(err);
      addLog("Bundle execution failed.");
      alert("Bundle execution failed");
    }

    setExecutingBundle(false);
  };

  const startAuto = async () => {
    if (!autoMint) return;
    try {
      await backend.post("/auto/start", { mint: autoMint });
      setAutoActive(true);
      addLog(`Auto mode started for mint ${autoMint}`);
    } catch (err) {
      console.error(err);
      addLog("Failed to start auto mode.");
      alert("Failed to start auto mode");
    }
  };

  const stopAuto = async () => {
    try {
      await backend.post("/auto/stop", { mint: autoMint });
      setAutoActive(false);
      addLog(`Auto mode stopped for mint ${autoMint}`);
    } catch (err) {
      console.error(err);
      addLog("Failed to stop auto mode.");
      alert("Failed to stop auto mode");
    }
  };

  return (
    <div className="app-root">
      {/* HEADER */}
      <header className="header">
        <div className="header-left">
          <h1>Solana Rug Sniper</h1>
          <span className="header-tagline">
            Real-time collapse sniping with Jito bundles
          </span>
        </div>

        <div className="header-right">
          {connected ? (
            <>
              <span className="status-dot online"></span>
              <span className="status-text">
                {publicKey.slice(0, 4)}...{publicKey.slice(-4)}
              </span>
              <button className="btn danger" onClick={disconnect}>
                Disconnect
              </button>
            </>
          ) : (
            <>
              <span className="status-dot offline"></span>
              <span className="status-text">Wallet not connected</span>
              <button className="btn" onClick={connect}>
                Connect Phantom
              </button>
            </>
          )}
        </div>
      </header>

      <div className="layout">
        {/* LEFT PANEL: Manual Sniper */}
        <section className="panel left-panel">
          <h2>Manual Sniper</h2>
          <p className="panel-subtitle">
            Analyze a token and execute a single BUY+SELL bundle.
          </p>

          <div className="card">
            <label className="label">Token Mint</label>
            <input
              type="text"
              className="input"
              placeholder="Enter token mint address"
              value={mint}
              onChange={(e) => setMint(e.target.value)}
            />

            <button className="btn" onClick={analyzeToken}>
              {loadingAnalysis ? "Analyzing..." : "Analyze"}
            </button>
          </div>

          {analysis && (
            <div className="card">
              <h3>Analysis</h3>
              <p>
                <span className="label-inline">Risk:</span>{" "}
                <span className={`risk-badge risk-${analysis.risk.status}`}>
                  {analysis.risk.status.toUpperCase()}
                </span>
              </p>
              <p>
                <span className="label-inline">Liquidity:</span>{" "}
                ${analysis.risk.liquidityUsd}
              </p>
              <p>
                <span className="label-inline">Price 5m:</span>{" "}
                {analysis.risk.priceChange5m}%
              </p>
              <p>
                <span className="label-inline">Price 1h:</span>{" "}
                {analysis.risk.priceChange1h}%
              </p>

              <h4>Reasons</h4>
              <ul className="reasons-list">
                {analysis.risk.reasons.map((r, i) => (
                  <li key={i}>{r}</li>
                ))}
              </ul>

              <h4>Plan</h4>
              {!analysis.plan.canSnipe && <p>‚ùå {analysis.plan.reason}</p>}

              {analysis.plan.canSnipe && (
                <>
                  <ul className="steps-list">
                    {analysis.plan.steps.map((s, i) => (
                      <li key={i}>
                        <span
                          className={`step-badge step-${s.type.toLowerCase()}`}
                        >
                          {s.type}
                        </span>{" "}
                        {s.amountSol || s.amount}
                      </li>
                    ))}
                  </ul>

                  <button
                    className="btn"
                    style={{ marginTop: "12px" }}
                    disabled={executingBundle || !connected}
                    onClick={() => executeBundle("manual", analysis)}
                  >
                    {executingBundle
                      ? "Executing Bundle..."
                      : "Execute BUY+SELL Bundle"}
                  </button>

                  {!connected && (
                    <p className="hint">
                      Connect Phantom to execute bundles.
                    </p>
                  )}
                </>
              )}
            </div>
          )}
        </section>

        {/* RIGHT PANEL: Auto Mode + Opportunities */}
        <section className="panel right-panel">
          <h2>Auto Mode</h2>
          <p className="panel-subtitle">
            Monitor a token and receive real-time snipe opportunities.
          </p>

          <div className="card">
            <label className="label">Auto Target Mint</label>
            <input
              type="text"
              className="input"
              placeholder="Mint to monitor"
              value={autoMint}
              onChange={(e) => setAutoMint(e.target.value)}
            />

            <div className="row">
              {!autoActive ? (
                <button className="btn" onClick={startAuto}>
                  Start Auto Mode
                </button>
              ) : (
                <button className="btn danger" onClick={stopAuto}>
                  Stop Auto Mode
                </button>
              )}
            </div>
          </div>

          <div className="card">
            <h3>Auto Opportunities</h3>
            {autoOpportunities.length === 0 && (
              <p className="hint">No opportunities detected yet.</p>
            )}

            {autoOpportunities.map((op, idx) => (
              <div key={idx} className="op-card">
                <div className="op-header">
                  <span className="op-mint">{op.mint}</span>
                  <span
                    className={`risk-badge risk-${op.risk.status}`}
                    style={{ marginLeft: "8px" }}
                  >
                    {op.risk.status.toUpperCase()}
                  </span>
                </div>
                <div className="op-body">
                  <p>Liquidity: ${op.risk.liquidityUsd}</p>
                  <p>
                    5m: {op.risk.priceChange5m}% | 1h: {op.risk.priceChange1h}%
                  </p>
                  <button
                    className="btn small"
                    disabled={executingBundle || !connected}
                    onClick={() => executeBundle("auto", op)}
                  >
                    Execute Bundle
                  </button>
                </div>
              </div>
            ))}
          </div>
        </section>
      </div>

      {/* LOGS */}
      <section className="logs-panel">
        <h2>Execution Logs</h2>
        <div className="logs-box">
          <pre>{log}</pre>
        </div>
      </section>
    </div>
  );
}
